var Common = require("chthonios/rt/Common");
var Environment = Common.Environment.load({});

/**
 * @class
 */
var StateStream = Environment.Class.extend(
/** @lends StateStream.prototype */
{
	is : null,
	os : null,

	/**
	 * Initializes the stream wrapper.
	 */
	init : function(is, os) {
		this.is = is;
		this.os = os;
	}
}, "StateStream");

module.exports = {
	/** @type Function */
	StateStream : StateStream
}